import React, { useState } from 'react';
import './components.css';
import Journal from "./journal";
import stickerComponent from '../assets/images/sticker_component.png';

function PageView() {
    const pages = [
        "Page 1",
        "Page 2",
        "Page 3",
        "Page 4"
    ];
    const [open, setOpen] = useState(false);

        const handleOpen = () => setOpen(true);
     const handleClose = () => setOpen(false);

    const backgroundImg = '../assets/images/journal-bg.png'

    return (
        <div className="App">
            <Journal pages={pages} backgroundImage={backgroundImg}/>
            <div className="addSticker" onClick={showModal}>
                <img src={stickerComponent}></img>
            </div>
            <Modal
            open={open}
            onClose={handleClose}
            aria-labelledby="modal-modal-title"
            aria-describedby="modal-modal-description"
            >
                <Box sx={style}>
                    <Typography id="modal-modal-title" variant="h6" component="h2">
                    Text in a modal
                    </Typography>
                    <Typography id="modal-modal-description" sx={{ mt: 2 }}>
                    Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
                    </Typography>
                </Box>
            </Modal>
        </div>
    );
}

export default PageView;
